(() => {
  // javascript/script.js
  function toggleMenu() {
    const menuId = this.getAttribute("aria-controls");
    const menu = document.getElementById(menuId);
    let menuState = menu.getAttribute("armch-menu-state");
    if (menuState === "collapsed") {
      menu.setAttribute("armch-menu-state", "expanded");
    } else {
      menu.setAttribute("armch-menu-state", "collapsed");
    }
  }
  var primaryMenuToggler = document.querySelector('.menu-toggler[aria-controls="primary-menu"]');
  primaryMenuToggler.addEventListener("click", toggleMenu);
  function colophonMenuIcons() {
    const menuItems = document.querySelectorAll(".widget_nav_menu .menu > .menu-item");
    menuItems.forEach((elem) => {
      let icon = document.createElement("i");
      icon.classList.add("fa-solid", "fa-caret-right");
      elem.prepend(icon);
    });
  }
  document.addEventListener("DOMContentLoaded", colophonMenuIcons);
})();
